import './App.css';
import { useState } from "react";

function App() {
  const [todo, setTodo] = useState("");
  const [todos, setTodos] = useState([]);
  const addToDo = () => {
    if (todo != "") {
    setTodos([...todos, todo])
    setTodo(""); 
    }
  }
  const deleteToDo = (text) => {
    const newTodos = todos.filter((todo) => {
      return todo !== text;
    });
    setTodos(newTodos);
  };


  return (
    <div className="App">
      <h1>React To Do App</h1>
      <div className = "input-wrapper">
        <input type="text" name="todo" value = {todo} placeholder="create a new todo" onChange = {(e) => {setTodo(e.target.value);}} />
        <button className='AddButton' onClick = {addToDo}>Add</button>
      </div>
      {todos?.length > 0 ? (
      <ul className='todolist'>
        {todos.map((todo, index) => (
          <div className="todo">
            <li key = {index}> {todo} </li>
            <button className='delete-button' onClick={() => {deleteToDo(todo)}}>Delete</button>
            <br />
          </div>
        ))}
      </ul>
      ) : (
        <div className='empty'>
          <p>No Task Found</p>
          </div>
      )}
    </div>
  );
}

export default App;
